      username: ***
      password: ***
    postgre:
      driver-class-name: org.postgresql.Driver
      jdbc-url: jdbc:postgresql://****:****/****
      username: ***
      password: ***
  jpa:
    hibernate:
      ddl-auto: update
    properties:
      hibernate:
        temp:
          use_jdbc_metadata_defaults: false
        mysql-dialect: org.hibernate.dialect.MySQL5Dialect
        postgre-dialect: org.hibernate.dialect.PostgreSQLDialect
    show-sql: true
同时需要配置数据源和扫描信息
@Configuration
public class DataSourceConfig {
    @Bean(name = "dataSourceMysql")
    @Primary
    @ConfigurationProperties(prefix = "spring.datasource.mysql")
    public DataSource dataSourceMysql(){
        return (DataSource) DataSourceBuilder.create().build();
    }

    @Bean(name = "dataSourcePostgre")
    @ConfigurationProperties(prefix = "spring.datasource.postgre")
    public DataSource dataSourcePostgre() {
        return (DataSource) DataSourceBuilder.create().build();
    }
}

以mysql数据源的配置为例
@Configuration
@EntityScan(basePackages = "com.sccba.jpa.entity.mysql")
@EnableJpaRepositories(
       basePackages = "com.sccba.jpa.dao.mysql",
       entityManagerFactoryRef = "mysqlEntityManagerFactoryBean",
       transactionManagerRef = "mysqlTransactionManager")
@EnableTransactionManagement
public class MysqlSourceConfig {

   //第一个数据源，可以不加Qualifier，第二个必须加
   @Autowired
   @Qualifier("dataSourceMysql")
   private DataSource dataSource;

   //jpa其他参数配置
   @Autowired
   private JpaProperties jpaProperties;

   //实体管理工厂builder
   @Autowired
   private EntityManagerFactoryBuilder factoryBuilder;

   //配置第一个实体管理工厂的bean，加入要扫描的实体包等
   @Bean(name = "mysqlEntityManagerFactoryBean")
   @Primary
   public LocalContainerEntityManagerFactoryBean entityManagerFactoryBean() {
       return factoryBuilder.dataSource(dataSource)
               .properties(jpaProperties.getProperties())
               .packages("com.sccba.jpa.entity.mysql")
               .persistenceUnit("mysqlPersistenceUnit")
               .build();
   }

   @Bean(name = "mysqlEntityManager")
   @Primary
   public EntityManager entityManager() {
       return entityManagerFactoryBean().getObject().createEntityManager();
   }

   // jpa事务管理
   @Bean(name = "mysqlTransactionManager")
   @Primary
   public JpaTransactionManager transactionManager() {
       JpaTransactionManager jpaTransactionManager = new JpaTransactionManager();
       jpaTransactionManager.setEntityManagerFactory(entityManagerFactoryBean().getObject());
       return jpaTransactionManager;
   }
}
